import React, { useEffect, useState } from "react";
import axios from "axios";

import "./index.css";

const TransactionStatistics = ({ month }) => {
  const [statistics, setStatistics] = useState(null);
  const date = "2021-12";

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3000/statistics?month=${date}`
        );
        setStatistics(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [month]);

  return (
    <div className="statistics-container">
      <h1>{`Statistics - ${month}`}</h1>
      <div className="data-container">
        {statistics ? (
          <>
            <div>
              <p>
                Total Sale <span>{statistics.totalSaleAmount}</span>
              </p>
            </div>
            <div>
              <p>
                Total Sold item <span> {statistics.totalSoldItems}</span>
              </p>
            </div>
            <div>
              <p>
                Total not sold item <span> {statistics.totalNotSoldItems}</span>
              </p>
            </div>
          </>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default TransactionStatistics;
