import React, { useState, useEffect } from "react";
import "./index.css"; // Make sure to create a CSS file for styling

const TransactionsTable = () => {
  const [transactions, setTransactions] = useState([]);
  const [selectedMonth, setSelectedMonth] = useState("March");
  const [currentPage, setCurrentPage] = useState(1);

  // Fetch data from the API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `http://localhost:3000/transactions?page=${currentPage}?month=${selectedMonth}`
        );
        const data = await response.json();
        setTransactions(data.transactions);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [currentPage, selectedMonth]);

  // Create an array with 10 empty items
  const emptyRows = Array.from({ length: 10 });

  return (
    <div className="transactions-table-container">
      <div className="transactions-container">
        {/* ... (existing code) */}
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Description</th>
                <th>Price</th>
                <th>Category</th>
                <th>Sold</th>
                <th>Image</th>
              </tr>
            </thead>
            <tbody>
              {transactions.length > 0
                ? transactions.map((transaction) => (
                    <tr key={transaction.id}>{/* ... (existing code) */}</tr>
                  ))
                : emptyRows.map((_, index) => (
                    <tr key={index}>
                      <td>&nbsp;</td>
                      <td>&nbsp;</td>
                      <td>&nbsp;</td>
                      <td>&nbsp;</td>
                      <td>&nbsp;</td>
                      <td>&nbsp;</td>
                      <td>&nbsp;</td>
                    </tr>
                  ))}
            </tbody>
          </table>
        </div>

        {/* ... (existing code) */}
      </div>
    </div>
  );
};

export default TransactionsTable;
