import React from "react";

import "./index.css";

const BarChart = ({ month }) => {
  const { dataSet } = props;

  const labels = dataSet.map((item) => item.topic);
  const intensityData = dataSet.map((item) => item.intensity);
  const likelihoodData = dataSet.map((item) => item.likelihood);
  const relevanceData = dataSet.map((item) => item.relevance);

  const sectors = Array.from(new Set(dataSet.map((item) => item.topic)));
  const sectorCounts = sectors.map((sector) => {
    const count = dataSet.filter((item) => item.sector === sector).length;
    return count;
  });

  const data = {
    labels: sectors,
    datasets: [
      {
        label: "Intensity",
        data: intensityData,
        backgroundColor: "rgba(255, 99, 132, 0.5)",
      },
      {
        label: "likelihood",
        data: likelihoodData,
        backgroundColor: "rgba(53, 162, 235, 0.5)",
      },
      {
        label: "relevance",
        data: relevanceData,
        backgroundColor: "rgba(200, 192, 235, 0.5)",
      },
    ],
  };
  return (
    <div className="bar-chart-container">
      <h1>{`Bar Chart Stats - ${month}`}</h1>
      <div className="chart-container">BarChart</div>
    </div>
  );
};

export default BarChart;
