import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";

import "./index.css";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const options = {
  responsive: true,
  plugins: {
    legend: {
      position: "top",
    },
    title: {
      display: true,
      text: "Bar Chart",
    },
  },
};

const BarChart = () => {
  const [chartData, setChartData] = useState(null);
  const month = "2021-12";

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3000/bar-chart?month=${month}`
        );
        const chartDataResponse = response.data;

        // Split values into labels and data
        const labels = Object.keys(chartDataResponse);
        const data = Object.values(chartDataResponse);
        const dataValues = data.map((value) => value * 20);

        setChartData({
          labels: labels,
          datasets: [
            {
            
              data: dataValues,
              backgroundColor: "rgba(75,192,192,0.2)",
              borderColor: "rgba(75,192,192,1)",
              borderWidth: 1,
            },
          ],
        });
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [month]);

  return (
    <div className="bar-chart-container">
      <h1>{`Bar Chart Stats - ${month}`}</h1>
      <div className="chart-container">
        {chartData ? (
          <Bar options={options} data={chartData} />
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default BarChart;
