import React, { useState, useEffect } from "react";
import "./index.css"; // Make sure to create a CSS file for styling

const TransactionsTable = () => {
  const [transactions, setTransactions] = useState([]);
  const [selectedMonth, setSelectedMonth] = useState("March");
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);

  // Fetch data from the API
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(false);
        const response = await fetch(
          `http://localhost:3000/transactions?page=${currentPage}?month=${selectedMonth}`
        );
        const data = await response.json();
        setTransactions(data.transactions);
        setLoading(true);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [currentPage, selectedMonth]);

  const handlePrevPage = () => {
    // Prevent setting currentPage to zero or negative values
    setCurrentPage((prev) => (prev > 1 ? prev - 1 : 1));
  };

  return (
    <div className="transactions-table-container">
      <div className="transactions-container">
        <div className="transactions-header">
          <h2>Transactions Dashboard</h2>
        </div>

        <div className="search-container">
          <button className="search-button">Search Transaction</button>
          <select
            className="select"
            value={selectedMonth}
            onChange={(e) => setSelectedMonth(e.target.value)}
          >
            <option value="January">January</option>
            <option value="February">February</option>
            <option value="March">March</option>
            <option value="April">April</option>
            <option value="May">May</option>
            <option value="June">June</option>
            <option value="July">July</option>
            <option value="August">August</option>
            <option value="September">September</option>
            <option value="October">October</option>
            <option value="November">November</option>
            <option value="December">December</option>
          </select>
        </div>

        {loading ? (
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Title</th>
                  <th>Description</th>
                  <th>Price</th>
                  <th>Category</th>
                  <th>Sold</th>
                  <th>Image</th>
                </tr>
              </thead>
              <tbody>
                {transactions.map((transaction) => (
                  <tr key={transaction.id}>
                    <td>{transaction.id}</td>
                    <td>{transaction.title}</td>
                    <td>{transaction.description}</td>
                    <td>{transaction.price}</td>
                    <td>{transaction.category}</td>
                    <td>{`${transaction.sold ? "Sold" : "UnSold"}`}</td>
                    <td>
                      <img
                        className="image"
                        src={transaction.image}
                        alt="Transaction"
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p>"Loading"</p>
        )}

        <div className="pagination">
          <p>{`Page No:${currentPage}`}</p>
          <div>
            <button onClick={() => setCurrentPage((prev) => prev - 1)}>
              Previous
            </button>
            <button onClick={() => setCurrentPage((prev) => prev + 1)}>
              Next
            </button>
          </div>
          <p>Per Page:10</p>
        </div>
      </div>
    </div>
  );
};

export default TransactionsTable;
